set_password() {
    expect -f - $* <<EOF
    	set user [lindex \$argv 0]
        set newpassword [lindex \$argv 1]

	log_user 0   # make expect less verbose
        eval spawn -noecho ssh $username@$nuc_ip_addr "passwd" \$user
        expect {
            "New password:" {
                send "\$newpassword\r"
		exp_continue
            }            
            "Retype password:" {
                send "\$newpassword\r"
		exp_continue
	    }
	    eof {
	        catch wait result
		exit [lindex \$result 3]
	    }
        }
EOF
}


set_root_and_skill_passwords() {
